let data = [
    {id: 1, name: 'Task 1'},
    {id: 2, name: 'Task 1'},
    {id: 3, name: 'Task 1'},
    {id: 4, name: 'Task 1'},
    {id: 5, name: 'Task 1'},
    {id: 6, name: 'Task 1'},
];

export function listReducer(state, action) {
    switch (action.type) {
        case 'ADD_ITEM':
            return [...state, action.item];
        case 'REMOVE_ITEM':
            return state.filter((item) => item.id !== action.id);
        default:
            return state;
    }
}
