let data = [
    {id: 1, name: 'Task 1'},
    {id: 2, name: 'Task 1'},
    {id: 3, name: 'Task 1'},
    {id: 4, name: 'Task 1'},
    {id: 5, name: 'Task 1'},
    {id: 6, name: 'Task 1'},
];

export function addItem(item) {
    data.push(item);
}

export function removeItem(id) {
    data = data.filter((item) => item.id !== id);
}

export function getData() {
    return data;
}
